<script setup>
import { onMounted } from 'vue';


onMounted(() => {
    const svgWelcomeSection = document.querySelector('#svg-welcome-section');

    if(!sessionStorage.getItem('animationPlayed')){

        svgWelcomeSection.classList.remove('hidden');
        svgWelcomeSection.classList.add('flex');

        setTimeout(() => {
            svgWelcomeSection.classList.add('animate__fadeOut')
        }, 4000)

        setTimeout(() => {
            svgWelcomeSection.remove();
        }, 6000)

        sessionStorage.setItem('animationPlayed', 'true');
    }
})

window.addEventListener('beforeunload', () => {
    sessionStorage.removeItem('animationPlayed');
});

</script>

